                <div style={{ display: "flex", justifyContent: "flex-end" }}>
                    <button
                        onClick={() => handleApplyJob(job)}
                        disabled={isApplied}
                        style={{
                            padding: "10px 24px",
                            borderRadius: "8px",
                            fontWeight: "500",
                            transition: "all 0.3s ease",
                            border: "none",
                            cursor: isApplied ? "not-allowed" : "pointer",
                            background: isApplied ? "#6b7280" : "linear-gradient(135deg, #3b82f6, #10b981)",
                            color: "#ffffff",
                            display: "flex",
                            alignItems: "center",
                            gap: "8px",
                            fontSize: "14px",
                        }}
                        onMouseOver={(e) => {
                            if (!isApplied) {
                                e.target.style.transform = "translateY(-1px)"
                                e.target.style.boxShadow = "0 4px 12px rgba(59, 130, 246, 0.4)"
                            }
                        }}
                        onMouseOut={(e) => {
                            if (!isApplied) {
                                e.target.style.transform = "translateY(0)"
                                e.target.style.boxShadow = "none"
                            }
                        }}
                    >
                        {isApplied ? (
                            <>
                                <CheckCircle style={{ width: "16px", height: "16px" }} />
                                Applied
                            </>
                        ) : (
                            "Apply Now"
                        )}
                    </button>
                </div>
            </div>
        )
    }

    const AppliedJobCard = ({ job }) => {
        const isWaiting = job.status.includes("Waiting") || job.status === "Assessment Completed"
        return (
            <div
                style={{
                    backgroundColor: "#1e293b",
                    borderRadius: "12px",
                    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.3)",
                    border: "1px solid #334155",
                    padding: "24px",
                    marginBottom: "16px",
                    transition: "all 0.3s ease",
                }}
            >
                <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                    <div style={{ display: "flex", alignItems: "center", gap: "16px" }}>
                        <div
                            style={{
                                width: "40px",
                                height: "40px",
                                background: isWaiting
                                    ? "linear-gradient(135deg, #f59e0b, #ef4444)"
                                    : "linear-gradient(135deg, #3b82f6, #10b981)",
                                borderRadius: "8px",
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                fontSize: "18px",
                            }}
                        >
                            {isWaiting ? "â³" : "ðŸ’¼"}
                        </div>
                        <div>
                            <h3 style={{ fontSize: "18px", fontWeight: "600", color: "#ffffff", margin: "0 0 4px 0" }}>
                                {job.title}
                            </h3>
                            <p style={{ color: "#cbd5e1", fontSize: "14px", margin: "0 0 4px 0" }}>{job.company}</p>
                            <p style={{ fontSize: "12px", color: "#e2e8f0", margin: 0 }}>
                                Applied on: {new Date(job.appliedDate).toLocaleDateString()}
                            </p>
                            {job.waitingMessage && (
                                <div
                                    style={{
                                        marginTop: "8px",
                                        padding: "8px 12px",
                                        backgroundColor: "#334155",
                                        borderRadius: "6px",
                                        border: "1px solid #f59e0b",
                                    }}
                                >
                                    <div style={{ display: "flex", alignItems: "center", gap: "6px" }}>
                                        <Hourglass style={{ width: "14px", height: "14px", color: "#f59e0b" }} />
                                        <span style={{ fontSize: "12px", color: "#f59e0b", fontWeight: "500" }}>{job.waitingMessage}</span>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                    <div style={{ display: "flex", alignItems: "center", gap: "12px" }}>
                        <span
                            style={{
                                padding: "6px 12px",
                                borderRadius: "20px",
                                fontSize: "12px",
                                fontWeight: "500",
                                ...job.statusColor,
                                display: "flex",
                                alignItems: "center",
                                gap: "6px",
                            }}
                        >
                            {isWaiting && <Hourglass style={{ width: "12px", height: "12px" }} />}
