                        </span>
                        <button
                            onClick={() => handleViewDetails(job)}
                            style={{
                                color: "#3b82f6",
                                fontWeight: "500",
                                fontSize: "14px",
                                padding: "8px 16px",
                                border: "1px solid #334155",
                                borderRadius: "8px",
                                backgroundColor: "#1e293b",
                                cursor: "pointer",
                                transition: "all 0.3s ease",
                                display: "flex",
                                alignItems: "center",
                                gap: "6px",
                            }}
                            onMouseOver={(e) => {
                                e.target.style.backgroundColor = "#334155"
                                e.target.style.borderColor = "#3b82f6"
                                e.target.style.transform = "translateY(-1px)"
                            }}
                            onMouseOut={(e) => {
                                e.target.style.backgroundColor = "#1e293b"
                                e.target.style.borderColor = "#334155"
                                e.target.style.transform = "translateY(0)"
                            }}
                        >
                            <Eye style={{ width: "14px", height: "14px" }} />
                            View Details
                        </button>
                    </div>
                </div>
            </div>
        )
    }

    // Assessment Modal Component
    const AssessmentModal = () => {
        const question = aptitudeQuestions[currentQuestion]
        const progress = ((currentQuestion + 1) / aptitudeQuestions.length) * 100
        const answeredQuestions = Object.keys(answers).length

        if (!assessmentStarted && !assessmentCompleted) {
            return (
                <div
                    style={{
                        position: "fixed",
                        inset: 0,
                        backgroundColor: "rgba(0, 0, 0, 0.8)",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        zIndex: 50,
                        padding: "16px",
                    }}
                >
                    <div
                        style={{
                            backgroundColor: "#1e293b",
                            borderRadius: "12px",
                            boxShadow: "0 25px 50px -12px rgba(0, 0, 0, 0.5)",
                            maxWidth: "600px",
                            width: "100%",
                            padding: "32px",
                            textAlign: "center",
                            border: "1px solid #334155",
                        }}
                    >
                        <div style={{ fontSize: "48px", marginBottom: "16px" }}>ðŸ“</div>
                        <h2 style={{ fontSize: "24px", fontWeight: "bold", color: "#ffffff", marginBottom: "16px" }}>
                            Aptitude Assessment
                        </h2>
                        <p style={{ color: "#cbd5e1", marginBottom: "8px", fontSize: "16px" }}>
                            {selectedInterview?.company} - {selectedInterview?.title}
                        </p>
                        <p style={{ color: "#e2e8f0", marginBottom: "24px", fontSize: "14px", lineHeight: "1.6" }}>
                            This assessment contains {aptitudeQuestions.length} questions covering quantitative, logical, and verbal
                            reasoning. You have 30 minutes to complete all questions.
                        </p>
                        <div
                            style={{
                                backgroundColor: "#334155",
                                padding: "16px",
                                borderRadius: "8px",
                                marginBottom: "24px",
                                textAlign: "left",
                            }}
                        >
                            <h4 style={{ fontSize: "14px", fontWeight: "600", color: "#ffffff", marginBottom: "8px" }}>
                                Instructions:
                            </h4>
                            <ul style={{ fontSize: "12px", color: "#cbd5e1", margin: 0, paddingLeft: "16px" }}>
                                <li>Read each question carefully before selecting your answer</li>
                                <li>You can navigate between questions using Next/Previous buttons</li>
                                <li>Make sure to answer all questions before submitting</li>
                                <li>The timer will start once you begin the assessment</li>
                            </ul>
                        </div>
                        <div style={{ display: "flex", gap: "12px", justifyContent: "center" }}>
                            <button
                                onClick={() => setShowAssessment(false)}
                                style={{
                                    padding: "12px 24px",
                                    border: "1px solid #334155",
                                    borderRadius: "8px",
                                    backgroundColor: "#1e293b",
                                    color: "#e2e8f0",
                                    cursor: "pointer",
                                    fontSize: "14px",
                                    fontWeight: "500",
                                    transition: "all 0.3s ease",
                                }}
                                onMouseOver={(e) => {
                                    e.target.style.backgroundColor = "#334155"
                                }}
                                onMouseOut={(e) => {
                                    e.target.style.backgroundColor = "#1e293b"
                                }}
                            >
                                Cancel
                            </button>
                            <button
                                onClick={startAssessment}
                                style={{
                                    padding: "12px 24px",
                                    borderRadius: "8px",
                                    background: "linear-gradient(135deg, #3b82f6, #10b981)",
                                    color: "#ffffff",
                                    border: "none",
                                    cursor: "pointer",
                                    fontSize: "14px",
                                    fontWeight: "500",
                                    transition: "all 0.3s ease",
                                }}
                                onMouseOver={(e) => {
                                    e.target.style.transform = "translateY(-1px)"
                                    e.target.style.boxShadow = "0 4px 12px rgba(59, 130, 246, 0.4)"
                                }}
                                onMouseOut={(e) => {
                                    e.target.style.transform = "translateY(0)"
                                    e.target.style.boxShadow = "none"
                                }}
                            >
                                Start Assessment
                            </button>
                        </div>
                    </div>
                </div>
            )
        }

        if (assessmentCompleted) {
            const score = aptitudeQuestions.reduce((acc, q) => {
                return acc + (answers[q.id] === q.correct ? 1 : 0)
            }, 0)
            const percentage = Math.round((score / aptitudeQuestions.length) * 100)

            return (
                <div
                    style={{
                        position: "fixed",
                        inset: 0,
                        backgroundColor: "rgba(0, 0, 0, 0.8)",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        zIndex: 50,
                        padding: "16px",
                    }}
                >
                    <div
                        style={{
                            backgroundColor: "#1e293b",
                            borderRadius: "12px",
                            boxShadow: "0 25px 50px -12px rgba(0, 0, 0, 0.5)",
                            maxWidth: "500px",
                            width: "100%",
                            padding: "32px",
                            textAlign: "center",
                            border: "1px solid #334155",
                        }}
                    >
                        <div style={{ fontSize: "48px", marginBottom: "16px" }}>ðŸŽ‰</div>
                        <h2 style={{ fontSize: "24px", fontWeight: "bold", color: "#ffffff", marginBottom: "16px" }}>
                            Assessment Completed!
                        </h2>
                        <div style={{ backgroundColor: "#334155", padding: "20px", borderRadius: "8px", marginBottom: "24px" }}>
                            <div style={{ fontSize: "32px", fontWeight: "bold", color: "#10b981", marginBottom: "8px" }}>
                                {score}/{aptitudeQuestions.length}
                            </div>
                            <div style={{ fontSize: "18px", color: "#10b981", fontWeight: "600" }}>{percentage}% Score</div>
                        </div>
                        <p style={{ color: "#cbd5e1", marginBottom: "24px", fontSize: "14px" }}>
                            Thank you for completing the assessment. Your results will be reviewed and you'll be notified about the
                            next steps within 2-3 business days.
                        </p>
                        <button
                            onClick={() => setShowAssessment(false)}
                            style={{
                                padding: "12px 24px",
                                borderRadius: "8px",
                                background: "linear-gradient(135deg, #3b82f6, #10b981)",
                                color: "#ffffff",
                                border: "none",
                                cursor: "pointer",
                                fontSize: "14px",
                                fontWeight: "500",
                                transition: "all 0.3s ease",
                            }}
                            onMouseOver={(e) => {
                                e.target.style.transform = "translateY(-1px)"
                                e.target.style.boxShadow = "0 4px 12px rgba(59, 130, 246, 0.4)"
                            }}
                            onMouseOut={(e) => {
                                e.target.style.transform = "translateY(0)"
                                e.target.style.boxShadow = "none"
                            }}
                        >
                            Close
                        </button>
                    </div>
                </div>
            )
        }

        return (
            <div
                style={{
                    position: "fixed",
                    inset: 0,
                    backgroundColor: "rgba(0, 0, 0, 0.8)",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    zIndex: 50,
                    padding: "16px",
                }}
            >
                <div
                    style={{
                        backgroundColor: "#1e293b",
                        borderRadius: "12px",
                        boxShadow: "0 25px 50px -12px rgba(0, 0, 0, 0.5)",
                        maxWidth: "800px",
                        width: "100%",
                        maxHeight: "90vh",
                        display: "flex",
                        flexDirection: "column",
                        border: "1px solid #334155",
                    }}
                >
                    {/* Header */}
                    <div
                        style={{
                            padding: "24px",
                            borderBottom: "1px solid #334155",
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center",
                        }}
                    >
                        <div>
                            <h2 style={{ fontSize: "20px", fontWeight: "bold", color: "#ffffff", margin: "0 0 4px 0" }}>
                                Aptitude Assessment
                            </h2>
                            <p style={{ fontSize: "14px", color: "#cbd5e1", margin: 0 }}>
                                {selectedInterview?.company} - {selectedInterview?.title}
                            </p>
                        </div>
                        <div style={{ display: "flex", alignItems: "center", gap: "16px" }}>
                            <div style={{ display: "flex", alignItems: "center", gap: "8px", color: "#ef4444" }}>
                                <Timer style={{ width: "16px", height: "16px" }} />
                                <span style={{ fontSize: "16px", fontWeight: "600" }}>{formatTime(timeLeft)}</span>
                            </div>
                        </div>
                    </div>

                    {/* Progress Bar */}
                    <div style={{ padding: "16px 24px", borderBottom: "1px solid #334155" }}>
                        <div
                            style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "8px" }}
                        >
                            <span style={{ fontSize: "14px", color: "#cbd5e1" }}>
                                Question {currentQuestion + 1} of {aptitudeQuestions.length}
                            </span>
                            <span style={{ fontSize: "14px", color: "#cbd5e1" }}>
                                Answered: {answeredQuestions}/{aptitudeQuestions.length}
                            </span>
                        </div>
                        <div style={{ width: "100%", backgroundColor: "#334155", borderRadius: "4px", height: "8px" }}>
                            <div
                                style={{
                                    width: `${progress}%`,
                                    background: "linear-gradient(135deg, #3b82f6, #10b981)",
                                    borderRadius: "4px",
                                    height: "100%",
                                    transition: "width 0.3s ease",
                                }}
                            />
                        </div>
                    </div>

                    {/* Question Content */}
                    <div style={{ flex: 1, padding: "24px", overflowY: "auto" }}>
                        <div style={{ marginBottom: "16px" }}>
                            <div style={{ display: "flex", alignItems: "center", gap: "8px", marginBottom: "16px" }}>
                                <span
                                    style={{
                                        backgroundColor:
                                            question.type === "quantitative"
                                                ? "#3b82f6"
                                                : question.type === "logical"
                                                    ? "#f59e0b"
                                                    : "#10b981",
                                        color: "#ffffff",
                                        padding: "4px 8px",
                                        borderRadius: "12px",
                                        fontSize: "12px",
                                        fontWeight: "500",
                                        textTransform: "capitalize",
                                    }}
                                >
                                    {question.type}
                                </span>
                            </div>
                            <h3
                                style={{
                                    fontSize: "18px",
                                    fontWeight: "600",
                                    color: "#ffffff",
                                    lineHeight: "1.5",
                                    margin: "0 0 24px 0",
                                }}
                            >
                                {question.question}
                            </h3>
                        </div>
                        <div style={{ display: "flex", flexDirection: "column", gap: "12px" }}>
                            {question.options.map((option, index) => (
                                <button
                                    key={index}
                                    onClick={() => handleAnswerSelect(question.id, index)}
                                    style={{
                                        padding: "16px",
                                        border: `2px solid ${answers[question.id] === index ? "#3b82f6" : "#334155"}`,
                                        borderRadius: "8px",
                                        backgroundColor: answers[question.id] === index ? "#334155" : "#1e293b",
                                        color: answers[question.id] === index ? "#3b82f6" : "#e2e8f0",
                                        cursor: "pointer",
                                        textAlign: "left",
                                        fontSize: "14px",
                                        transition: "all 0.3s ease",
                                        display: "flex",
                                        alignItems: "center",
                                        gap: "12px",
                                    }}
                                    onMouseOver={(e) => {
                                        if (answers[question.id] !== index) {
                                            e.target.style.borderColor = "#6b7280"
                                            e.target.style.backgroundColor = "#334155"
                                        }
                                    }}
                                    onMouseOut={(e) => {
                                        if (answers[question.id] !== index) {
                                            e.target.style.borderColor = "#334155"
                                            e.target.style.backgroundColor = "#1e293b"
                                        }
                                    }}
                                >
                                    <div
                                        style={{
                                            width: "20px",
                                            height: "20px",
                                            borderRadius: "50%",
                                            border: `2px solid ${answers[question.id] === index ? "#3b82f6" : "#6b7280"}`,
                                            backgroundColor: answers[question.id] === index ? "#3b82f6" : "transparent",
                                            display: "flex",
                                            alignItems: "center",
                                            justifyContent: "center",
                                            flexShrink: 0,
                                        }}
                                    >
                                        {answers[question.id] === index && (
                                            <div style={{ width: "8px", height: "8px", borderRadius: "50%", backgroundColor: "#ffffff" }} />
                                        )}
                                    </div>
                                    <span>
                                        {String.fromCharCode(65 + index)}. {option}
                                    </span>
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Footer */}
                    <div
                        style={{
                            padding: "24px",
                            borderTop: "1px solid #334155",
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center",
                        }}
                    >
                        <button
                            onClick={prevQuestion}
                            disabled={currentQuestion === 0}
                            style={{
                                display: "flex",
                                alignItems: "center",
                                gap: "8px",
                                padding: "10px 16px",
                                border: "1px solid #334155",
                                borderRadius: "8px",
                                backgroundColor: currentQuestion === 0 ? "#334155" : "#1e293b",
                                color: currentQuestion === 0 ? "#6b7280" : "#e2e8f0",
                                cursor: currentQuestion === 0 ? "not-allowed" : "pointer",
                                fontSize: "14px",
                                fontWeight: "500",
                                transition: "all 0.3s ease",
                            }}
                            onMouseOver={(e) => {
                                if (currentQuestion !== 0) {
                                    e.target.style.backgroundColor = "#334155"
                                }
                            }}
                            onMouseOut={(e) => {
                                if (currentQuestion !== 0) {
                                    e.target.style.backgroundColor = "#1e293b"
                                }
                            }}
                        >
                            <ArrowLeft style={{ width: "16px", height: "16px" }} />
                            Previous
                        </button>
                        <div style={{ display: "flex", gap: "12px" }}>
                            {currentQuestion === aptitudeQuestions.length - 1 ? (
                                <button
                                    onClick={handleSubmitAssessment}
                                    style={{
                                        display: "flex",
                                        alignItems: "center",
                                        gap: "8px",
                                        padding: "10px 20px",
                                        borderRadius: "8px",
                                        backgroundColor: "#ef4444",
                                        color: "#ffffff",
                                        border: "none",
                                        cursor: "pointer",
                                        fontSize: "14px",
                                        fontWeight: "500",
                                        transition: "all 0.3s ease",
                                    }}
                                    onMouseOver={(e) => {
                                        e.target.style.backgroundColor = "#dc2626"
                                        e.target.style.transform = "translateY(-1px)"
                                    }}
                                    onMouseOut={(e) => {
                                        e.target.style.backgroundColor = "#ef4444"
                                        e.target.style.transform = "translateY(0)"
                                    }}
                                >
                                    Submit Assessment
                                </button>
                            ) : (
                                <button
                                    onClick={nextQuestion}
                                    style={{
                                        display: "flex",
                                        alignItems: "center",
                                        gap: "8px",
                                        padding: "10px 16px",
                                        borderRadius: "8px",
                                        background: "linear-gradient(135deg, #3b82f6, #10b981)",
                                        color: "#ffffff",
                                        border: "none",
                                        cursor: "pointer",
                                        fontSize: "14px",
                                        fontWeight: "500",
                                        transition: "all 0.3s ease",
                                    }}
                                    onMouseOver={(e) => {
                                        e.target.style.transform = "translateY(-1px)"
                                        e.target.style.boxShadow = "0 4px 12px rgba(59, 130, 246, 0.4)"
                                    }}
                                    onMouseOut={(e) => {
                                        e.target.style.transform = "translateY(0)"
                                        e.target.style.boxShadow = "none"
                                    }}
                                >
                                    Next
                                    <ArrowRight style={{ width: "16px", height: "16px" }} />
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    // Job Details Modal Component
    const JobDetailsModal = () => (
        <div
            style={{
                position: "fixed",
                inset: 0,
                backgroundColor: "rgba(0, 0, 0, 0.8)",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                zIndex: 50,
                padding: "16px",
