                        </div>
                        <div>
                            {interviews.map((interview) => (
                                <InterviewCard key={interview.id} interview={interview} />
                            ))}
                        </div>
                    </div>
                )
            default:
                return null
        }
    }

    return (
        <div
            style={{
                minHeight: "100vh",
                backgroundColor: "#0f172a",
                display: "flex",
                fontFamily: "system-ui, -apple-system, sans-serif",
            }}
        >
            {/* Left Sidebar */}
            <div
                style={{
                    width: "256px",
                    backgroundColor: "#1e293b",
                    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.3)",
                    borderRight: "1px solid #334155",
                    display: "flex",
                    flexDirection: "column",
                    height: "100vh",
                    position: "fixed",
                }}
            >
                {/* Header */}
                <div style={{ padding: "24px", borderBottom: "1px solid #334155" }}>
                    <h2 style={{ fontSize: "20px", fontWeight: "bold", color: "#ffffff", margin: "0 0 4px 0" }}>
                        E-Campus Placement
                    </h2>
                    <p style={{ fontSize: "14px", color: "#cbd5e1", margin: 0 }}>Career Portal</p>
                </div>

                {/* Navigation */}
                <nav style={{ flex: 1, paddingTop: "24px" }}>
                    {sidebarItems.map((item) => {
                        const Icon = item.icon
                        const isActive = activeSection === item.id
                        return (
                            <button
                                key={item.id}
                                onClick={() => setActiveSection(item.id)}
                                style={{
                                    width: "100%",
                                    display: "flex",
                                    alignItems: "center",
                                    padding: "12px 24px",
                                    textAlign: "left",
                                    border: "none",
                                    backgroundColor: isActive ? "#334155" : "transparent",
                                    color: isActive ? "#3b82f6" : "#e2e8f0",
                                    borderRight: isActive ? "3px solid #3b82f6" : "none",
                                    cursor: "pointer",
                                    transition: "all 0.3s ease",
                                    fontSize: "14px",
                                    fontWeight: isActive ? "500" : "400",
                                    justifyContent: "space-between",
                                }}
                                onMouseOver={(e) => {
                                    if (!isActive) {
                                        e.target.style.backgroundColor = "#334155"
                                        e.target.style.color = "#3b82f6"
                                    }
                                }}
                                onMouseOut={(e) => {
                                    if (!isActive) {
                                        e.target.style.backgroundColor = "transparent"
                                        e.target.style.color = "#e2e8f0"
                                    }
                                }}
                            >
                                <div style={{ display: "flex", alignItems: "center" }}>
                                    <Icon style={{ width: "20px", height: "20px", marginRight: "12px" }} />
                                    {item.label}
                                </div>
                                {item.id === "applied" && appliedJobs.length > 0 && (
                                    <span
                                        style={{
                                            background: "linear-gradient(135deg, #3b82f6, #10b981)",
                                            color: "#ffffff",
                                            fontSize: "12px",
                                            borderRadius: "10px",
                                            padding: "2px 8px",
                                            minWidth: "20px",
                                            textAlign: "center",
                                        }}
                                    >
                                        {appliedJobs.length}
                                    </span>
                                )}
                            </button>
                        )
                    })}
                </nav>

                {/* Help Button */}
                <div style={{ padding: "24px", borderTop: "1px solid #334155" }}>
                    <button
                        onClick={() => setShowHelp(true)}
                        style={{
                            width: "100%",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            padding: "12px 16px",
                            background: "linear-gradient(135deg, #3b82f6, #10b981)",
                            color: "#ffffff",
                            borderRadius: "8px",
                            border: "none",
                            cursor: "pointer",
                            fontWeight: "500",
                            fontSize: "14px",
                            transition: "all 0.3s ease",
                        }}
                        onMouseOver={(e) => {
                            e.target.style.transform = "translateY(-1px)"
                            e.target.style.boxShadow = "0 4px 12px rgba(59, 130, 246, 0.4)"
                        }}
                        onMouseOut={(e) => {
                            e.target.style.transform = "translateY(0)"
                            e.target.style.boxShadow = "none"
                        }}
                    >
                        <HelpCircle style={{ width: "20px", height: "20px", marginRight: "8px" }} />
                        Help & Support
                    </button>
                </div>
            </div>

            {/* Main Content */}
            <div style={{ flex: 1, marginLeft: "256px", padding: "32px", overflowY: "auto" }}>{renderContent()}</div>

            {/* Modals */}
            {showHelp && <HelpModal />}
            {showJobDetails && <JobDetailsModal />}
            {showAssessment && <AssessmentModal />}
        </div>
    )
}

export default PlacementPortal
