"use client"

import { useState } from "react"

// Stationary Page Component
const StationaryPage = ({ setCurrentPage, cartItems, addToCart, getCartCount }) => {
    const [selectedCategory, setSelectedCategory] = useState("books-notebooks")
    const [showAlert, setShowAlert] = useState(false)
    const [alertMessage, setAlertMessage] = useState("")
    const cartCount = getCartCount()

    // Stationary categories
    const categories = [
        { id: "books-notebooks", name: "Books & Notebooks", icon: "📚" },
        { id: "writing-instruments", name: "Writing Instruments", icon: "✏️" },
        { id: "art-supplies", name: "Art Supplies", icon: "🎨" },
        { id: "office-supplies", name: "Office Supplies", icon: "📎" },
        { id: "electronics", name: "Electronics & Accessories", icon: "🔌" },
    ]

    // Stationary items for each category
    const stationaryItems = {
        "books-notebooks": [
            {
                id: 1,
                name: "University Record Book",
                description: "Official university record book with 200 pages, ruled format",
                price: 45,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 2,
                name: "Blue Book (Exam)",
                description: "Standard blue book for examinations, 16 pages, college approved",
                price: 15,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 3,
                name: "Spiral Notebook A4",
                description: "A4 size spiral notebook, 300 pages, ruled, hard cover",
                price: 85,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 4,
                name: "Graph Book",
                description: "Graph notebook with grid lines, 200 pages, perfect for engineering",
                price: 55,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 5,
                name: "Lab Record Book",
                description: "Laboratory record book with blank pages, 100 pages, thick paper",
                price: 65,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 6,
                name: "Sticky Notes Pack",
                description: "Colorful sticky notes pack, 5 colors, 100 sheets each",
                price: 35,
                category: "Books & Notebooks",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        "writing-instruments": [
            {
                id: 7,
                name: "Blue Ballpoint Pen (Pack of 10)",
                description: "Smooth writing blue ballpoint pens, reliable ink flow",
                price: 50,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 8,
                name: "Black Gel Pen Set",
                description: "Premium black gel pens, pack of 5, smooth writing experience",
                price: 75,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 9,
                name: "Mechanical Pencil 0.5mm",
                description: "Professional mechanical pencil with lead refills included",
                price: 40,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 10,
                name: "Highlighter Set",
                description: "Fluorescent highlighters, 6 colors, perfect for studying",
                price: 60,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 11,
                name: "Fountain Pen",
                description: "Classic fountain pen with ink cartridges, elegant writing",
                price: 120,
                category: "Writing Instruments",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        "art-supplies": [
            {
                id: 12,
                name: "Colored Pencils Set",
                description: "Professional colored pencils, 24 colors, vibrant pigments",
                price: 180,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 13,
                name: "Sketch Pad A3",
                description: "High-quality sketch pad, 50 sheets, 200gsm paper",
                price: 95,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 14,
                name: "Watercolor Paint Set",
                description: "Student watercolor set, 12 colors with brushes included",
                price: 150,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 15,
                name: "Drawing Compass Set",
                description: "Geometry compass set with pencil and pen attachments",
                price: 85,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 16,
                name: "Eraser & Sharpener Kit",
                description: "Premium erasers and sharpeners, various sizes included",
                price: 45,
                category: "Art Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        "office-supplies": [
            {
                id: 17,
                name: "Paper Clips & Pins Set",
                description: "Assorted paper clips, safety pins, and push pins",
                price: 30,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 18,
                name: "Stapler with Staples",
                description: "Heavy-duty stapler with 1000 staples included",
                price: 120,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 19,
                name: "File Folders Pack",
                description: "A4 file folders, pack of 10, assorted colors",
                price: 80,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 20,
                name: "Transparent Tape Roll",
                description: "Clear adhesive tape, 2 rolls, strong adhesion",
                price: 25,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 21,
                name: "Ruler & Scale Set",
                description: "Plastic rulers, 15cm and 30cm, with triangular scales",
                price: 35,
                category: "Office Supplies",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
        electronics: [
            {
                id: 22,
                name: "Scientific Calculator",
                description: "Advanced scientific calculator with 240+ functions",
                price: 450,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 23,
                name: "USB Flash Drive 32GB",
                description: "High-speed USB 3.0 flash drive, 32GB storage capacity",
                price: 280,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 24,
                name: "Power Bank 10000mAh",
                description: "Portable power bank with dual USB ports, fast charging",
                price: 850,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 25,
                name: "Earphones with Mic",
                description: "Wired earphones with built-in microphone, good sound quality",
                price: 320,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
            {
                id: 26,
                name: "Phone Stand Holder",
                description: "Adjustable phone stand for desk, compatible with all phones",
                price: 150,
                category: "Electronics & Accessories",
                image: "/placeholder.svg?height=120&width=120",
            },
        ],
    }

    const handleAddToCart = (item) => {
        addToCart(item)
        setAlertMessage(`${item.name} added to cart!`)
        setShowAlert(true)
        setTimeout(() => {
            setShowAlert(false)
        }, 2000)
    }

    const styles = {
        container: {
            minHeight: "100vh",
            background: "linear-gradient(135deg, #0f172a, #1e293b)",
            fontFamily: "system-ui, -apple-system, sans-serif",
            color: "white",
        },
        header: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            padding: "1rem",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
            position: "sticky",
            top: 0,
            zIndex: 50,
        },
        headerTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        cartContainer: {
            position: "relative",
            background: "rgba(255, 255, 255, 0.2)",
            borderRadius: "50%",
            width: "40px",
            height: "40px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            cursor: "pointer",
            transition: "all 0.3s ease",
        },
        cartIcon: {
            fontSize: "1.25rem",
            color: "white",
        },
        cartBadge: {
            position: "absolute",
            top: "-8px",
            right: "-8px",
            background: "#ef4444",
            color: "white",
            borderRadius: "50%",
            width: "20px",
            height: "20px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "0.75rem",
            fontWeight: "bold",
        },
        mainContent: {
            display: "flex",
            minHeight: "calc(100vh - 80px)",
        },
        sidebar: {
            width: "280px",
            background: "rgba(15, 23, 42, 0.95)",
            backdropFilter: "blur(12px)",
            borderRight: "1px solid #475569",
            padding: "1rem 0",
            overflowY: "auto",
        },
        categoryItem: {
            padding: "1rem 1.5rem",
            cursor: "pointer",
            transition: "all 0.3s ease",
            borderLeft: "4px solid transparent",
            display: "flex",
            alignItems: "center",
            gap: "0.75rem",
        },
        categoryItemActive: {
            background: "linear-gradient(90deg, rgba(59, 130, 246, 0.2), rgba(16, 185, 129, 0.2))",
            borderLeftColor: "#60a5fa",
            color: "#60a5fa",
        },
        categoryIcon: {
            fontSize: "1.25rem",
        },
        categoryName: {
            fontSize: "0.95rem",
            fontWeight: 500,
        },
        contentArea: {
            flex: 1,
            padding: "2rem",
            overflowY: "auto",
        },
        itemsGrid: {
            display: "grid",
            gap: "1.5rem",
        },
        stationaryItem: {
            background: "rgba(30, 41, 59, 0.8)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "1.5rem",
            border: "1px solid #475569",
            display: "flex",
            gap: "1.5rem",
            transition: "all 0.3s ease",
        },
        itemImage: {
            width: "120px",
            height: "120px",
            borderRadius: "0.75rem",
            objectFit: "cover",
            flexShrink: 0,
        },
        itemDetails: {
            flex: 1,
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
        },
        itemName: {
            fontSize: "1.25rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
            marginBottom: "0.5rem",
        },
        itemDescription: {
            color: "#cbd5e1",
            fontSize: "0.95rem",
            lineHeight: 1.5,
            marginBottom: "1rem",
        },
        itemFooter: {
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
        },
        itemPrice: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "#60a5fa",
        },
        addButton: {
            background: "linear-gradient(135deg, #ef4444, #dc2626)",
            border: "none",
            borderRadius: "2rem",
            padding: "0.75rem 1.5rem",
            color: "white",
            fontSize: "1rem",
            fontWeight: "bold",
            cursor: "pointer",
            transition: "all 0.3s ease",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
        },
        alert: {
            position: "fixed",
            top: "100px",
            left: "50%",
            transform: "translateX(-50%)",
            background: "linear-gradient(135deg, #10b981, #059669)",
            color: "white",
            padding: "1rem 2rem",
            borderRadius: "2rem",
            boxShadow: "0 8px 25px rgba(16, 185, 129, 0.4)",
            zIndex: 100,
            fontSize: "1rem",
            fontWeight: "bold",
            opacity: showAlert ? 1 : 0,
            visibility: showAlert ? "visible" : "hidden",
            transition: "all 0.3s ease",
        },
    }

    return (
        <div style={styles.container}>
            {/* Alert */}
            <div style={styles.alert}>{alertMessage}</div>

            {/* Header */}
            <header style={styles.header}>
                <button
                    style={{
                        background: "rgba(255, 255, 255, 0.2)",
                        border: "none",
                        borderRadius: "50%",
                        width: "40px",
                        height: "40px",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        cursor: "pointer",
                        color: "white",
                        fontSize: "1.25rem",
                        transition: "all 0.3s ease",
                    }}
                    onClick={() => alert("Welcome to DSCE Stationary Store!")}
                >
                    🏠
                </button>
                <h1 style={styles.headerTitle}>DSCE Stationary Store</h1>
                <div style={{ ...styles.cartContainer }} onClick={() => setCurrentPage("cart")}>
                    <span style={styles.cartIcon}>🛒</span>
                    {cartCount > 0 && <span style={styles.cartBadge}>{cartCount}</span>}
                </div>
            </header>

            {/* Main Content */}
            <div style={styles.mainContent}>
                {/* Sidebar */}
                <div style={styles.sidebar}>
                    {categories.map((category) => (
                        <div
                            key={category.id}
                            style={{
                                ...styles.categoryItem,
                                ...(selectedCategory === category.id ? styles.categoryItemActive : {}),
                            }}
                            onClick={() => setSelectedCategory(category.id)}
                        >
                            <span style={styles.categoryIcon}>{category.icon}</span>
                            <span style={styles.categoryName}>{category.name}</span>
                        </div>
                    ))}
                </div>

                {/* Content Area */}
                <div style={styles.contentArea}>
                    <div style={styles.itemsGrid}>
                        {stationaryItems[selectedCategory]?.map((item) => (
                            <div key={item.id} style={styles.stationaryItem}>
                                <img src={item.image || "/placeholder.svg"} alt={item.name} style={styles.itemImage} />
                                <div style={styles.itemDetails}>
                                    <div>
                                        <h3 style={styles.itemName}>{item.name}</h3>
                                        <p style={styles.itemDescription}>{item.description}</p>
                                    </div>
                                    <div style={styles.itemFooter}>
                                        <span style={styles.itemPrice}>₹{item.price}/-</span>
                                        <button style={styles.addButton} onClick={() => handleAddToCart(item)}>
                                            Add +
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}

// Cart Page Component
const CartPage = ({ setCurrentPage, cartItems, removeFromCart, updateQuantity }) => {
    // Calculate totals
    const [customerInfo, setCustomerInfo] = useState({
        name: "",
        studentId: "",
        phone: "",
        email: "",
    })

    const handleInputChange = (e) => {
        setCustomerInfo({
            ...customerInfo,
            [e.target.name]: e.target.value,
        })
    }

    // Calculate totals
    const subtotal = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0)
    const tax = Math.round(subtotal * 0.05) // 5% tax
    const total = subtotal + tax // Remove deliveryCharge

    const handleQuantityChange = (id, change) => {
        updateQuantity(id, change)
    }

    const handleRemoveItem = (id) => {
        removeFromCart(id)
    }

    const handleProceedToPay = () => {
        if (!customerInfo.name || !customerInfo.studentId || !customerInfo.phone) {
            alert("Please fill in all required fields!")
            return
        }
        const orderData = {
            subtotal,
            tax,
            total,
            itemCount: cartItems.reduce((sum, item) => sum + item.quantity, 0),
            customerInfo,
            items: cartItems,
        }
        setCurrentPage("payment", orderData)
    }

    const styles = {
        container: {
            minHeight: "100vh",
            background: "linear-gradient(135deg, #0f172a, #1e293b)",
            fontFamily: "system-ui, -apple-system, sans-serif",
            color: "white",
        },
        header: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            padding: "1rem",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
            position: "sticky",
            top: 0,
            zIndex: 50,
        },
        headerTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        cartCount: {
            background: "rgba(255, 255, 255, 0.2)",
            borderRadius: "50%",
            width: "40px",
            height: "40px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "1rem",
            fontWeight: "bold",
        },
        mainContent: {
            padding: "2rem",
            maxWidth: "1200px",
            margin: "0 auto",
        },
        cartSection: {
            background: "rgba(30, 41, 59, 0.8)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "2rem",
            border: "1px solid #475569",
            marginBottom: "2rem",
        },
        sectionTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "#60a5fa",
            marginBottom: "1.5rem",
            display: "flex",
            alignItems: "center",
            gap: "0.5rem",
        },
        emptyCart: {
            textAlign: "center",
            padding: "3rem",
            color: "#94a3b8",
        },
        emptyCartIcon: {
            fontSize: "4rem",
            marginBottom: "1rem",
        },
        emptyCartText: {
            fontSize: "1.25rem",
            marginBottom: "1rem",
        },
        customerForm: {
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
            gap: "1rem",
        },
        inputGroup: {
            display: "flex",
            flexDirection: "column",
            gap: "0.5rem",
        },
        inputLabel: {
            color: "#cbd5e1",
            fontSize: "0.9rem",
            fontWeight: 500,
        },
        inputField: {
            background: "rgba(15, 23, 42, 0.8)",
            border: "1px solid #475569",
            borderRadius: "0.5rem",
            padding: "0.75rem",
            color: "white",
            fontSize: "1rem",
            transition: "all 0.3s ease",
            outline: "none",
        },
        billSummary: {
            background: "rgba(15, 23, 42, 0.9)",
            borderRadius: "0.75rem",
            padding: "1.5rem",
            border: "1px solid #334155",
        },
        billRow: {
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            padding: "0.5rem 0",
            borderBottom: "1px solid #334155",
        },
        billRowTotal: {
            borderBottom: "2px solid #60a5fa",
            fontSize: "1.25rem",
            fontWeight: "bold",
            color: "#60a5fa",
        },
        proceedButton: {
            background: "linear-gradient(135deg, #10b981, #059669)",
            border: "none",
            borderRadius: "2rem",
            padding: "1rem 2rem",
            color: "white",
            fontSize: "1.125rem",
            fontWeight: "bold",
            cursor: "pointer",
            transition: "all 0.3s ease",
            width: "100%",
            marginTop: "1rem",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
        },
    }

    if (cartItems.length === 0) {
        return (
            <div style={styles.container}>
                <header style={styles.header}>
                    <button
                        style={{
                            background: "rgba(255, 255, 255, 0.2)",
                            border: "none",
                            borderRadius: "50%",
                            width: "40px",
                            height: "40px",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            cursor: "pointer",
                            color: "white",
                            fontSize: "1.25rem",
                            transition: "all 0.3s ease",
                        }}
                        onClick={() => setCurrentPage("stationary")}
                    >
                        ←
                    </button>
                    <h1 style={styles.headerTitle}>Your Cart</h1>
                    <div style={styles.cartCount}>0</div>
                </header>
                <div style={styles.mainContent}>
                    <div style={styles.cartSection}>
                        <div style={styles.emptyCart}>
                            <div style={styles.emptyCartIcon}>🛒</div>
                            <div style={styles.emptyCartText}>Your cart is empty</div>
                            <p>Add some useful stationary items from our store!</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    return (
        <div style={styles.container}>
            <header style={styles.header}>
                <button
                    style={{
                        background: "rgba(255, 255, 255, 0.2)",
                        border: "none",
                        borderRadius: "50%",
                        width: "40px",
                        height: "40px",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        cursor: "pointer",
                        color: "white",
                        fontSize: "1.25rem",
                        transition: "all 0.3s ease",
                    }}
                    onClick={() => setCurrentPage("stationary")}
                >
                    ←
                </button>
                <h1 style={styles.headerTitle}>Your Cart</h1>
                <div style={styles.cartCount}>{cartItems.length}</div>
            </header>

            <div style={styles.mainContent}>
                <div style={styles.cartSection}>
                    <h2 style={styles.sectionTitle}>📦 Order Items ({cartItems.length})</h2>
                    {cartItems.map((item) => (
                        <div
                            key={item.id}
                            style={{
                                background: "rgba(15, 23, 42, 0.8)",
                                borderRadius: "0.75rem",
                                padding: "1.5rem",
                                marginBottom: "1rem",
                                border: "1px solid #334155",
                                display: "flex",
                                gap: "1.5rem",
                                alignItems: "center",
                            }}
                        >
                            <img
                                src={item.image || "/placeholder.svg"}
                                alt={item.name}
                                style={{
                                    width: "80px",
                                    height: "80px",
                                    borderRadius: "0.5rem",
                                    objectFit: "cover",
                                    flexShrink: 0,
                                }}
                            />
                            <div style={{ flex: 1 }}>
                                <div
                                    style={{
                                        fontSize: "1.25rem",
                                        fontWeight: "bold",
                                        color: "white",
                                        marginBottom: "0.5rem",
                                    }}
                                >
                                    {item.name}
                                </div>
                                <div
                                    style={{
                                        color: "#cbd5e1",
                                        fontSize: "0.9rem",
                                        marginBottom: "0.5rem",
                                    }}
                                >
                                    {item.description}
                                </div>
                                <div
                                    style={{
                                        color: "#60a5fa",
                                        fontSize: "0.8rem",
                                        fontWeight: 500,
                                    }}
                                >
                                    {item.category}
                                </div>
                            </div>
                            <div
                                style={{
                                    display: "flex",
                                    alignItems: "center",
                                    gap: "1rem",
                                }}
                            >
                                <div
                                    style={{
                                        display: "flex",
                                        alignItems: "center",
                                        gap: "0.5rem",
                                        background: "rgba(59, 130, 246, 0.2)",
                                        borderRadius: "2rem",
                                        padding: "0.5rem",
                                    }}
                                >
                                    <button
                                        style={{
                                            background: "#3b82f6",
                                            border: "none",
                                            borderRadius: "50%",
                                            width: "30px",
                                            height: "30px",
                                            color: "white",
                                            cursor: "pointer",
                                            display: "flex",
                                            alignItems: "center",
                                            justifyContent: "center",
                                            fontSize: "1rem",
                                            fontWeight: "bold",
                                        }}
                                        onClick={() => handleQuantityChange(item.id, -1)}
                                    >
                                        −
                                    </button>
                                    <span
                                        style={{
                                            minWidth: "30px",
                                            textAlign: "center",
                                            fontWeight: "bold",
                                            color: "white",
                                        }}
                                    >
                                        {item.quantity}
                                    </span>
                                    <button
                                        style={{
                                            background: "#3b82f6",
                                            border: "none",
                                            borderRadius: "50%",
                                            width: "30px",
                                            height: "30px",
                                            color: "white",
                                            cursor: "pointer",
                                            display: "flex",
                                            alignItems: "center",
                                            justifyContent: "center",
                                            fontSize: "1rem",
                                            fontWeight: "bold",
                                        }}
                                        onClick={() => handleQuantityChange(item.id, 1)}
                                    >
                                        +
                                    </button>
                                </div>
                                <div
                                    style={{
                                        fontSize: "1.25rem",
                                        fontWeight: "bold",
                                        color: "#10b981",
                                        minWidth: "80px",
                                        textAlign: "right",
                                    }}
                                >
                                    ₹{item.price * item.quantity}/-
                                </div>
                                <button
                                    style={{
                                        background: "#ef4444",
                                        border: "none",
                                        borderRadius: "0.5rem",
                                        padding: "0.5rem 1rem",
                                        color: "white",
                                        cursor: "pointer",
                                        fontSize: "0.9rem",
                                        transition: "all 0.3s ease",
                                    }}
                                    onClick={() => handleRemoveItem(item.id)}
                                >
                                    Remove
                                </button>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Customer Information */}
                <div style={styles.cartSection}>
                    <h2 style={styles.sectionTitle}>👤 Customer Information</h2>
                    <div style={styles.customerForm}>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Full Name *</label>
                            <input
                                type="text"
                                name="name"
                                value={customerInfo.name}
                                onChange={handleInputChange}
                                style={styles.inputField}
                                placeholder="Enter your full name"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Student ID *</label>
                            <input
                                type="text"
                                name="studentId"
                                value={customerInfo.studentId}
                                onChange={handleInputChange}
                                style={styles.inputField}
                                placeholder="Enter your student ID"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Phone Number *</label>
                            <input
                                type="tel"
                                name="phone"
                                value={customerInfo.phone}
                                onChange={handleInputChange}
                                style={styles.inputField}
                                placeholder="Enter your phone number"
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label style={styles.inputLabel}>Email (Optional)</label>
                            <input
                                type="email"
                                name="email"
                                value={customerInfo.email}
                                onChange={handleInputChange}
                                style={styles.inputField}
                                placeholder="Enter your email"
                            />
                        </div>
                    </div>
                </div>

                {/* Bill Summary */}
                <div style={styles.cartSection}>
                    <h2 style={styles.sectionTitle}>💰 Bill Summary</h2>
                    <div style={styles.billSummary}>
                        <div style={styles.billRow}>
                            <span>Subtotal ({cartItems.reduce((sum, item) => sum + item.quantity, 0)} items)</span>
                            <span>₹{subtotal}/-</span>
                        </div>
                        <div style={styles.billRow}>
                            <span>GST (5%)</span>
                            <span>₹{tax}/-</span>
                        </div>
                        <div style={{ ...styles.billRow, ...styles.billRowTotal }}>
                            <span>Total Amount</span>
                            <span>₹{total}/-</span>
                        </div>
                    </div>
                    <button style={styles.proceedButton} onClick={handleProceedToPay}>
                        Proceed to Pay - ₹{total}/-
                    </button>
                </div>
            </div>
        </div>
    )
}

// Payment Page Component
const PaymentPage = ({ setCurrentPage, orderData }) => {
    const [selectedPaymentMethod, setSelectedPaymentMethod] = useState("upi")
    const [paymentDetails, setPaymentDetails] = useState({
        upiId: "",
        cardNumber: "",
        cardExpiry: "",
        cardCvv: "",
        cardName: "",
    })
    const [isProcessing, setIsProcessing] = useState(false)

    const paymentMethods = [
        { id: "upi", name: "UPI Payment", icon: "📱", description: "Pay using UPI ID" },
        { id: "card", name: "Credit/Debit Card", icon: "💳", description: "Pay using your card" },
        { id: "cod", name: "Cash on Delivery", icon: "💵", description: "Pay when you receive" },
    ]

    const handleInputChange = (e) => {
        setPaymentDetails({
            ...paymentDetails,
            [e.target.name]: e.target.value,
        })
    }

    const handlePayment = () => {
        if (selectedPaymentMethod === "upi" && !paymentDetails.upiId) {
            alert("Please enter your UPI ID")
            return
        }
        if (selectedPaymentMethod === "card") {
            if (
                !paymentDetails.cardNumber ||
                !paymentDetails.cardExpiry ||
                !paymentDetails.cardCvv ||
                !paymentDetails.cardName
            ) {
                alert("Please fill in all card details")
                return
            }
        }

        setIsProcessing(true)
        // Simulate payment processing
        setTimeout(() => {
            setIsProcessing(false)
            setCurrentPage("confirmation")
        }, 3000)
    }

    const styles = {
        container: {
            minHeight: "100vh",
            background: "linear-gradient(135deg, #0f172a, #1e293b)",
            fontFamily: "system-ui, -apple-system, sans-serif",
            color: "white",
        },
        header: {
            background: "linear-gradient(135deg, #3b82f6, #10b981)",
            padding: "1rem",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
            position: "sticky",
            top: 0,
            zIndex: 50,
        },
        headerTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "white",
            margin: 0,
        },
        backButton: {
            background: "rgba(255, 255, 255, 0.2)",
            border: "none",
            borderRadius: "50%",
            width: "40px",
            height: "40px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            cursor: "pointer",
            color: "white",
            fontSize: "1.25rem",
            transition: "all 0.3s ease",
        },
        mainContent: {
            padding: "2rem",
            maxWidth: "800px",
            margin: "0 auto",
        },
        section: {
            background: "rgba(30, 41, 59, 0.8)",
            backdropFilter: "blur(12px)",
            borderRadius: "1rem",
            padding: "2rem",
            border: "1px solid #475569",
            marginBottom: "2rem",
        },
        sectionTitle: {
            fontSize: "1.5rem",
            fontWeight: "bold",
            color: "#60a5fa",
            marginBottom: "1.5rem",
            display: "flex",
            alignItems: "center",
            gap: "0.5rem",
        },
        paymentMethod: {
            background: "rgba(15, 23, 42, 0.8)",
            border: "2px solid #334155",
            borderRadius: "0.75rem",
            padding: "1.5rem",
            marginBottom: "1rem",
            cursor: "pointer",
            transition: "all 0.3s ease",
            display: "flex",
            alignItems: "center",
            gap: "1rem",
        },
        paymentMethodActive: {
            borderColor: "#60a5fa",
            background: "rgba(59, 130, 246, 0.1)",
        },
        paymentIcon: {
            fontSize: "2rem",
        },
        paymentInfo: {
            flex: 1,
        },
        paymentName: {
            fontSize: "1.1rem",
            fontWeight: "bold",
            color: "white",
            marginBottom: "0.25rem",
        },
        paymentDescription: {
            color: "#cbd5e1",
            fontSize: "0.9rem",
        },
        inputGroup: {
            marginBottom: "1rem",
        },
        inputLabel: {
            display: "block",
            color: "#cbd5e1",
            fontSize: "0.9rem",
            fontWeight: 500,
            marginBottom: "0.5rem",
        },
        inputField: {
            width: "100%",
            background: "rgba(15, 23, 42, 0.8)",
            border: "1px solid #475569",
            borderRadius: "0.5rem",
            padding: "0.75rem",
            color: "white",
            fontSize: "1rem",
            transition: "all 0.3s ease",
            outline: "none",
        },
        payButton: {
            width: "100%",
            background: "linear-gradient(135deg, #10b981, #059669)",
            border: "none",
            borderRadius: "2rem",
            padding: "1rem 2rem",
            color: "white",
            fontSize: "1.125rem",
            fontWeight: "bold",
            cursor: "pointer",
            transition: "all 0.3s ease",
            textTransform: "uppercase",
            letterSpacing: "0.05em",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "0.5rem",
        },
        processingOverlay: {
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0, 0, 0, 0.8)",
            zIndex: 100,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            flexDirection: "column",
            gap: "1rem",
        },
        spinner: {
            width: "50px",
            height: "50px",
            border: "4px solid #334155",
            borderTop: "4px solid #10b981",
            borderRadius: "50%",
            animation: "spin 1s linear infinite",
        },
    }

    return (
        <div style={styles.container}>
            <header style={styles.header}>
                <button style={styles.backButton} onClick={() => setCurrentPage("cart")}>
                    ←
                </button>
                <h1 style={styles.headerTitle}>Payment</h1>
                <div style={{ width: "40px" }}></div>
            </header>

            <div style={styles.mainContent}>
                {/* Order Summary */}
                <div style={styles.section}>
                    <h2 style={styles.sectionTitle}>📋 Order Summary</h2>
                    <div style={{ marginBottom: "1rem" }}>
                        <div
                            style={{
                                display: "flex",
                                justifyContent: "space-between",
                                padding: "0.5rem 0",
                                borderBottom: "1px solid #334155",
                            }}
                        >
                            <span>Items ({orderData.itemCount})</span>
                            <span>₹{orderData.subtotal}/-</span>
                        </div>
                        <div
                            style={{
                                display: "flex",
                                justifyContent: "space-between",
                                padding: "0.5rem 0",
                                borderBottom: "1px solid #334155",
                            }}
                        >
                            <span>GST (5%)</span>
                            <span>₹{orderData.tax}/-</span>
                        </div>
                        <div
                            style={{
                                display: "flex",
                                justifyContent: "space-between",
                                padding: "1rem 0",
                                fontSize: "1.25rem",
                                fontWeight: "bold",
                                color: "#60a5fa",
                                borderBottom: "2px solid #60a5fa",
                            }}
                        >
                            <span>Total Amount</span>
                            <span>₹{orderData.total}/-</span>
                        </div>
                    </div>
                </div>

                {/* Payment Methods */}
                <div style={styles.section}>
                    <h2 style={styles.sectionTitle}>💳 Select Payment Method</h2>
                    {paymentMethods.map((method) => (
                        <div
                            key={method.id}
                            style={{
                                ...styles.paymentMethod,
                                ...(selectedPaymentMethod === method.id ? styles.paymentMethodActive : {}),
                            }}
                            onClick={() => setSelectedPaymentMethod(method.id)}
                        >
                            <span style={styles.paymentIcon}>{method.icon}</span>
                            <div style={styles.paymentInfo}>
                                <div style={styles.paymentName}>{method.name}</div>
                                <div style={styles.paymentDescription}>{method.description}</div>
                            </div>
                            <div style={{ color: selectedPaymentMethod === method.id ? "#60a5fa" : "#6b7280", fontSize: "1.5rem" }}>
                                {selectedPaymentMethod === method.id ? "●" : "○"}
                            </div>
                        </div>
                    ))}
                </div>

                {/* Payment Details */}
                {selectedPaymentMethod !== "cod" && (
                    <div style={styles.section}>
                        <h2 style={styles.sectionTitle}>
                            {selectedPaymentMethod === "upi" ? "📱 UPI Details" : "💳 Card Details"}
                        </h2>
                        {selectedPaymentMethod === "upi" && (
                            <div style={styles.inputGroup}>
                                <label style={styles.inputLabel}>UPI ID *</label>
                                <input
                                    type="text"
                                    name="upiId"
                                    value={paymentDetails.upiId}
                                    onChange={handleInputChange}
                                    style={styles.inputField}
                                    placeholder="yourname@upi"
                                    required
                                />
                            </div>
                        )}
                        {selectedPaymentMethod === "card" && (
                            <>
                                <div style={styles.inputGroup}>
                                    <label style={styles.inputLabel}>Cardholder Name *</label>
                                    <input
                                        type="text"
                                        name="cardName"
                                        value={paymentDetails.cardName}
                                        onChange={handleInputChange}
                                        style={styles.inputField}
                                        placeholder="Enter cardholder name"
                                        required
                                    />
                                </div>
                                <div style={styles.inputGroup}>
                                    <label style={styles.inputLabel}>Card Number *</label>
                                    <input
                                        type="text"
                                        name="cardNumber"
                                        value={paymentDetails.cardNumber}
                                        onChange={handleInputChange}
                                        style={styles.inputField}
                                        placeholder="1234 5678 9012 3456"
                                        maxLength="19"
                                        required
                                    />
                                </div>
                                <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "1rem" }}>
                                    <div style={styles.inputGroup}>
                                        <label style={styles.inputLabel}>Expiry Date *</label>
                                        <input
                                            type="text"
                                            name="cardExpiry"
                                            value={paymentDetails.cardExpiry}
                                            onChange={handleInputChange}
                                            style={styles.inputField}
                                            placeholder="MM/YY"
                                            maxLength="5"
                                            required
                                        />
                                    </div>
                                    <div style={styles.inputGroup}>
                                        <label style={styles.inputLabel}>CVV *</label>
                                        <input
                                            type="text"
                                            name="cardCvv"
                                            value={paymentDetails.cardCvv}
                                            onChange={handleInputChange}
                                            style={styles.inputField}
                                            placeholder="123"
                                            maxLength="3"
                                            required
                                        />
                                    </div>
                                </div>
                            </>
                        )}
                    </div>
                )}

                {/* Pay Button */}
                <button style={styles.payButton} onClick={handlePayment} disabled={isProcessing}>
                    {selectedPaymentMethod === "cod" ? "Place Order" : `Pay ₹{orderData.total}/-`}
                    {selectedPaymentMethod !== "cod" && <span>💳</span>}
                </button>
            </div>

            {/* Processing Overlay */}
            {isProcessing && (
                <div style={styles.processingOverlay}>
                    <div style={styles.spinner}></div>
                    <div style={{ color: "white", fontSize: "1.25rem", fontWeight: "bold" }}>
                        {selectedPaymentMethod === "cod" ? "Placing your order..." : "Processing payment..."}
                    </div>
                    <div style={{ color: "#cbd5e1", textAlign: "center" }}>
                        Please wait while we {selectedPaymentMethod === "cod" ? "confirm your order" : "process your payment"}
                    </div>
                </div>
            )}

            <style>
                {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}
            </style>
        </div>
    )
}

// Order Confirmation Page Component
const OrderConfirmationPage = ({ setCurrentPage, orderData }) => {
