                    <div style={styles.detailRow}>
                        <span>Payment Status:</span>
                        <span style={{ color: "#10b981", fontWeight: "bold" }}>✅ Confirmed</span>
                    </div>

                </div>

                {/* Action Buttons */}
                <div style={styles.actionButtons}>
                    <button style={{ ...styles.button, ...styles.secondaryButton }} onClick={() => setCurrentPage("stationary")}>
                        Order More
                    </button>
                    <button
                        style={{ ...styles.button, ...styles.primaryButton }}
                        onClick={() => alert("Order tracking feature coming soon!")}
                    >
                        Track Order
                    </button>
                </div>
            </div>
        </div>
    )
}

// Main App Component
const App = () => {
    const [currentPage, setCurrentPage] = useState("stationary")
    const [orderData, setOrderData] = useState(null)
    const [cartItems, setCartItems] = useState([])

    // Cart functions
    const addToCart = (item) => {
        setCartItems((prevItems) => {
            const existingItem = prevItems.find((cartItem) => cartItem.id === item.id)
            if (existingItem) {
                return prevItems.map((cartItem) =>
                    cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem,
                )
            } else {
                return [...prevItems, { ...item, quantity: 1 }]
            }
        })
    }

    const removeFromCart = (id) => {
        setCartItems((prevItems) => prevItems.filter((item) => item.id !== id))
    }

    const updateQuantity = (id, change) => {
        setCartItems((prevItems) =>
            prevItems
                .map((item) => {
                    if (item.id === id) {
                        const newQuantity = Math.max(0, item.quantity + change)
                        return newQuantity === 0 ? null : { ...item, quantity: newQuantity }
                    }
                    return item
                })
                .filter(Boolean),
        )
    }

    const clearCart = () => {
        setCartItems([])
    }

    const getCartCount = () => {
        return cartItems.reduce((total, item) => total + item.quantity, 0)
    }

    const getCartTotal = () => {
        return cartItems.reduce((total, item) => total + item.price * item.quantity, 0)
    }

    const handlePageChange = (page, data = null) => {
        setCurrentPage(page)
        if (data) {
            setOrderData(data)
        }
    }

    return (
        <div>
            {currentPage === "stationary" && (
                <StationaryPage
                    setCurrentPage={handlePageChange}
                    cartItems={cartItems}
                    addToCart={addToCart}
                    getCartCount={getCartCount}
                />
            )}
            {currentPage === "cart" && (
                <CartPage
                    setCurrentPage={handlePageChange}
                    cartItems={cartItems}
                    removeFromCart={removeFromCart}
                    updateQuantity={updateQuantity}
                />
            )}
            {currentPage === "payment" && <PaymentPage setCurrentPage={handlePageChange} orderData={orderData} />}
            {currentPage === "confirmation" && (
                <OrderConfirmationPage setCurrentPage={handlePageChange} orderData={orderData} />
            )}
        </div>
    )
}

export default App
